void delay(uint16_t time_us){
    TA2CTL = TASSEL__SMCLK | MC__UP | TACLR;
    TA2CCR0 = time_us;
    while(!(TA2CCTL0 & CCIFG));
    TA2CCTL0 &= ~CCIFG;
}

